import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Roles } from 'src/app/Interfaces/roles';
import { RolesService } from 'src/app/services/roles/roles.service';
import { UsersService } from 'src/app/services/users/users.service';
import { MatSnackBar } from '@angular/material/snack-bar';
@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss']
})
export class UserAddComponent implements OnInit {
  public roles: Roles[] | undefined;

  public addForm = new FormGroup({
    name: new FormControl('', [Validators.required]),
    secondLastName: new FormControl('', [Validators.required]),
    lastName: new FormControl('', [Validators.required]),
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required]),
    roleId: new FormControl('', [Validators.required])
  })
  constructor(private userServives: UsersService, private rolesServices: RolesService, private router: Router, private snackBar: MatSnackBar) { }

  ngOnInit(): void {
    this.addRoles()
  }

  async addUser() {
    if (!this.addForm.value) return;
    const res = await this.userServives.postRegister(this.addForm.value)
    this.router.navigate(['/app'])
    this.snackBar.open(res.message, '', {

      duration: 3000,
      panelClass: ['green-snack-bar'],
      verticalPosition: 'top',

    });
  }

  async addRoles() {
    const res = await this.rolesServices.getRoles()
    this.roles = res.data
  }
}

<div class="container mt-3">
    <mat-toolbar  style="background-color: #4cff75;">
        <span> Add New User </span> 

      </mat-toolbar>

    <form [formGroup]="addForm" (submit)="addUser()">
        <mat-card class="text-center mt-3" fxLayoutAlign="center center"> 

        <div>
            <mat-form-field  appearance="fill">    
                <mat-label>Name</mat-label>
                <input matInput  formControlName="name" placeholder="Name" type="name" >

            </mat-form-field>
        </div>
        <div>
            <mat-form-field  appearance="fill">
                <mat-label>Second Name</mat-label>
                <input matInput  type="secondLastName" formControlName="secondLastName" placeholder="Second Name">

              </mat-form-field>
        </div>
        <div>
            <mat-form-field  appearance="fill">
                <mat-label>Last Name</mat-label>
                <input matInput  type="lastName" formControlName="lastName" placeholder="Last Name">

              </mat-form-field>
        </div>
        <div>
        <mat-form-field appearance="fill">
                <mat-label>Roles</mat-label>
                <mat-select  formControlName="roleId">
                  <mat-option *ngFor="let role of roles" [value]="role.id">
                    {{role.name}}
                  </mat-option>
                </mat-select>
              </mat-form-field> 
        </div> 
        <div>
            <mat-form-field  appearance="fill">
                <mat-label>Email</mat-label>
                <input matInput  type="email" formControlName="email" placeholder="email">

              </mat-form-field>
        </div>
        <div>
            <mat-form-field  appearance="fill">
                <mat-label>Password</mat-label>
                <input matInput  type="password" formControlName="password" placeholder="password">

              </mat-form-field>
        </div>
        <div class="mx-auto">
            <button mat-raised-button color="primary">Add User</button>
        </div>
    </mat-card>
    </form>


</div>
