import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Users } from 'src/app/Interfaces/users';
import { AuthService } from 'src/app/services/auth/auth.service';
import { UsersService } from 'src/app/services/users/users.service';

@Component({
  selector: 'app-login-component',
  templateUrl: './login-component.component.html',
  styleUrls: ['./login-component.component.scss']
})
export class LoginComponentComponent implements OnInit {
  public token: string = '';
  public usersProfile: Users[] | undefined;


  public loginForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required])
  })

  constructor(private router: Router, private authService: AuthService, private userService: UsersService) { }

  ngOnInit(): void {
  }
  getoPrivate() {
    this.router.navigate(['/app'])
  }

  async login() {
    if (!this.loginForm.valid) return;
    const res = await this.authService.authenticate(this.loginForm.value);
    this.getoPrivate()
    this.token = res.data.token
    this.users(this.token)
  }

  users(token: string) {
    this.userService.getUsers(token)
      .subscribe(user => {


        this.usersProfile = user
      });
  }
}

<form [formGroup]="loginForm" (submit)="login()">
    <mat-card class="text-center mt-3" fxLayoutAlign="center center"> 

    <div>
        <mat-form-field class="example-form-field" appearance="fill">    
            <mat-label>E-mail</mat-label>
            <input matInput  formControlName="email" placeholder="email" type="email" >
            <mat-error *ngIf="loginForm.controls['email'].hasError('email') || false " >
                No es un correo válido
            </mat-error>
            <mat-error *ngIf="loginForm.controls['email'].hasError('required') || false " >
                Email es requerido
            </mat-error>
        </mat-form-field>
    </div>
    <div>
        <mat-form-field class="example-form-field" appearance="fill">
            <mat-label>Password</mat-label>
            <input matInput  type="password" formControlName="password" placeholder="contraseña">
            <mat-error *ngIf="loginForm.controls['password'].errors" >
                La contraseña no es valida
            </mat-error>
          </mat-form-field>
    </div>
    <div class="mx-auto">
    <button class="btn button-color-card mt-4  btn-lg" >
        Log in
  </button>
    </div>
</mat-card>
</form>
